mapper.delete:
DELETE FROM tb_config_word_field t
WHERE t.id IN

    #{id}

mapper.select:
SELECT 
FROM tb_config_word_field t
WHERE t.row_valid = 1

    AND t.id = #{id}


    AND t.config_word_code = #{configWordCode}


    AND t.code = #{code}


    AND t.name = #{name}


    AND t.byte_offset = #{byteOffset}


    AND t.bit_offset = #{bitOffset}


    AND t.bit_length = #{bitLength}


    AND t.value_type = #{valueType}


    AND t.endianness = #{endianness}


    AND t.description = #{description}


    AND t.create_time = #{createTime}


    AND t.create_by = #{createBy}


    AND t.modify_time = #{modifyTime}


    AND t.modify_by = #{modifyBy}


    AND t.row_version = #{rowVersion}

ORDER BY t.id DESC

SELECT 
FROM tb_config_word_field t
WHERE t.row_valid = 1


    AND t.config_word_code = #{configWordCode}


    AND t.code = #{code}


    AND t.name LIKE #{name}

ORDER BY t.id DESC

SELECT 
FROM tb_config_word_field t
WHERE t.row_valid = 1
AND t.config_word_code = #{configWordCode}
AND t.code = #{code}
LIMIT 1
