mapper.delete:
DELETE FROM tb_config_word_profile t
WHERE t.id IN

    #{id}

mapper.select:
SELECT 
FROM tb_config_word_profile t
WHERE t.row_valid = 1

    AND t.id = #{id}


    AND t.config_word_code = #{configWordCode}


    AND t.code = #{code}


    AND t.name = #{name}


    AND t.read_type = #{readType}


    AND t.read_id = #{readId}


    AND t.write_type = #{writeType}


    AND t.write_id = #{writeId}


    AND t.session_type = #{sessionType}


    AND t.security_level = #{securityLevel}


    AND t.timeout_ms = #{timeoutMs}


    AND t.retry_times = #{retryTimes}


    AND t.software_pn = #{softwarePn}


    AND t.software_build_ver = #{softwareBuildVer}


    AND t.description = #{description}


    AND t.create_time = #{createTime}


    AND t.create_by = #{createBy}


    AND t.modify_time = #{modifyTime}


    AND t.modify_by = #{modifyBy}


    AND t.row_version = #{rowVersion}

ORDER BY t.id DESC

SELECT 
FROM tb_config_word_profile t
WHERE t.row_valid = 1


    AND t.config_word_code = #{configWordCode}


    AND t.code = #{code}


    AND t.name LIKE #{name}

ORDER BY t.id DESC

SELECT 
FROM tb_config_word_profile t
WHERE t.row_valid = 1
AND t.config_word_code = #{configWordCode}
AND t.code = #{code}
LIMIT 1
